(function(){function vals(){return{client:window.PAYPAL_CLIENT_ID||"",m:window.PAYPAL_PLAN_MONTHLY_1499||"",y:window.PAYPAL_PLAN_ANNUAL_4999||"",l:(window.PAYPAL_PLAN_LIFETIME_100||"")}}
function load(cb){var v=vals();if(!v.client){console.warn("PAYPAL_CLIENT_ID vacío");cb&&cb(false);return}if(window.paypal){cb&&cb(true);return}var s=document.createElement('script');s.src="https://www.paypal.com/sdk/js?client-id="+encodeURIComponent(v.client)+"&currency=EUR&intent=capture&components=buttons,subscriptions";s.onload=function(){cb&&cb(true)};s.onerror=function(){cb&&cb(false)};document.head.appendChild(s)}
function ensureModal(){var m=document.getElementById('pp-modal');if(m)return m;m=document.createElement('div');m.id='pp-modal';m.className='modal';m.innerHTML='<div class="box"><div id="pp-widget"></div><div style="margin-top:8px;text-align:right"><a href="#" class="btn" id="pp-close">Cerrar</a></div></div>';document.body.appendChild(m);m.addEventListener('click',function(e){if(e.target.id==='pp-modal'||e.target.id==='pp-close'){m.classList.remove('show');document.getElementById("pp-widget").innerHTML=""}});return m}
function buy(kind,src,amount,onUnlock){var modal=ensureModal();modal.classList.add('show');load(function(ok){if(!ok){alert("PayPal no configurado");return}paypal.Buttons({createOrder:function(d,a){return a.order.create({purchase_units:[{amount:{value:amount.toFixed(2)}}]})},onApprove:function(d,a){return a.order.capture().then(function(){modal.classList.remove('show');onUnlock&&onUnlock()})},onError:function(){alert("Pago no completado")}}).render('#pp-widget')})}
function subsMount(){var v=vals(),map=[["btn-monthly",v.m],["btn-yearly",v.y],["btn-lifetime",v.l]];map.forEach(function(x){var id=x[0], pid=x[1], el=document.getElementById(id);if(!el)return;if(!pid){el.innerHTML='<div class="badge">Configura plan</div>';return}load(function(ok){if(!ok){el.innerHTML='<div class="badge">PayPal no configurado</div>';return}paypal.Buttons({createSubscription:function(d,a){return a.subscription.create({plan_id:pid})},onApprove:function(){alert("Suscripción activada")}}).render('#'+id)})})}
window.IBG_PAY={openPurchase:buy,mountSubs:subsMount};})();
